import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Button } from '@/components/ui/button';
import { Play, Pause, RotateCcw } from 'lucide-react';
import { cn } from '@/lib/utils';

const Index: React.FC = () => {
  const [seconds, setSeconds] = useState(0);
  const [isRunning, setIsRunning] = useState(false);

  const toggle = () => {
    setIsRunning(!isRunning);
  };

  const reset = () => {
    setSeconds(0);
    setIsRunning(false);
  };

  React.useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isRunning) {
      interval = setInterval(() => {
        setSeconds((prev) => prev + 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isRunning]);

  const formatTime = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-lovable-gray-50">
      <div className="text-center px-4">
        <motion.div
          className="font-display text-8xl md:text-9xl font-bold text-lovable-orange mb-8"
          animate={isRunning ? { scale: [1, 1.02, 1] } : {}}
          transition={{ duration: 1, repeat: Infinity, ease: "easeInOut" }}
        >
          {formatTime(seconds)}
        </motion.div>

        <p className="text-xl text-muted-foreground mb-12">
          {isRunning ? 'Time Remaining' : 'Ready to Start'}
        </p>

        <div className="flex gap-4 justify-center">
          <Button variant="hero" size="xl" onClick={toggle}>
            {isRunning ? <Pause className="w-5 h-5" /> : <Play className="w-5 h-5" />}
            {isRunning ? 'Pause' : 'Start'}
          </Button>
          <Button variant="heroOutline" size="xl" onClick={reset}>
            <RotateCcw className="w-5 h-5" />
            Reset
          </Button>
        </div>
      </div>
    </div>
  );
};

export default Index;
